import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  View,
  Text,
  Dimensions,
  TouchableOpacity,
} from "react-native";
import MapComponent from "../components/MapComponent";
import * as Location from "expo-location";
import { FontAwesome5 } from "@expo/vector-icons";

const { width: WIDTH, height: HEIGHT } = Dimensions.get("screen");

const ModifyReport = ({ parentCallback, report, EditReport }) => {
  const [address, setAddress] = useState({
    latitude: 27.516458,
    longitude: -109.948725,
  });
  const [textAddress, setTextAddress] = useState("");

  useEffect(() => {
    ReverseGeocode(address);
  }, []);

  let ReverseGeocode = async (ad) => {
    let data = await Location.reverseGeocodeAsync({
      latitude: ad.latitude,
      longitude: ad.longitude,
    });
    setTextAddress(
      data[0].street +
        " #" +
        data[0].name +
        ", col. " +
        data[0].district +
        " CP. " +
        data[0].postalCode
    );
  };

  const sendData = () => {
    parentCallback(null);
  };

  const closeReport = () => {
    EditReport("EditReportScreen", 1);
  };

  const delayReport = () => {
    EditReport("EditReportScreen", 3);
  };

  return (
    <View style={styles.container}>
      <View style={styles.cardHeader}>
        <Text style={styles.headerText}>Reporte #{report.id}</Text>
      </View>

      <View style={styles.cardContent}>
        <View style={styles.textCardContainer}>
          <View style={styles.addressContainer}>
            <Text style={styles.addressText}>{textAddress}</Text>
          </View>
        </View>

        <View style={styles.mapContainer}>
          <MapComponent parentLocation={address} parentCallback={null} />
        </View>

        <View style={styles.textCardContainer}>
          <View style={styles.buttonsContainer}>
            <TouchableOpacity style={styles.actionButton} onPress={closeReport}>
              <FontAwesome5
                name="tools"
                size={HEIGHT * 0.018}
                color="rgba(0,158,227,1)"
              />
              <Text style={styles.actionButtonText}>Cerrar reporte</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.actionButton} onPress={delayReport}>
              <FontAwesome5
                name="clock"
                size={HEIGHT * 0.018}
                color="#223995"
              />
              <Text style={[styles.actionButtonText, { color: "#223995" }]}>
                Dejar inconcluso
              </Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>

      <View style={styles.cardFooter}>
        <TouchableOpacity style={styles.goBackButton} onPress={sendData}>
          <Text style={styles.buttonText}>Volver</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

export default ModifyReport;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "stretch",
    justifyContent: "center",
    width: "100%",
    borderRadius: 10,
  },
  cardHeader: {
    flex: 2,
    alignItems: "center",
    justifyContent: "space-evenly",
    flexDirection: "row",
    marginBottom: 2,
    backgroundColor: "white",
    borderTopLeftRadius: 10,
    borderTopRightRadius: 10,
  },
  cardContent: {
    flex: 20,
    alignItems: "flex-start",
    justifyContent: "center",
    backgroundColor: "white",
  },
  addressText: {
    fontSize: HEIGHT * 0.018,
    color: "black",
    textAlign: "center",
    fontWeight: "bold",
    marginHorizontal: WIDTH * 0.02,
  },
  actionButtonText: {
    fontSize: HEIGHT * 0.018,
    color: "rgba(0,158,227,1)",
    textAlign: "center",
    fontWeight: "bold",
    marginHorizontal: WIDTH * 0.008,
  },
  cardFooter: {
    flex: 3,
    alignItems: "center",
    justifyContent: "flex-end",
    flexDirection: "row",
    marginTop: 2,
    backgroundColor: "white",
    borderBottomLeftRadius: 10,
    borderBottomRightRadius: 10,
  },
  headerText: {
    fontSize: HEIGHT * 0.022,
    fontWeight: "bold",
  },
  buttonText: {
    fontSize: HEIGHT * 0.02,
    alignSelf: "center",
    color: "white",
  },
  goBackButton: {
    backgroundColor: "#CCD1D1",
    width: "25%",
    height: HEIGHT * 0.048,
    borderRadius: 5,
    alignItems: "center",
    justifyContent: "center",
    marginRight: WIDTH * 0.02,
  },
  actionButton: {
    backgroundColor: "white",
    flexDirection: "row",
    width: "42%",
    height: "50%",
    borderRadius: 5,
    alignItems: "center",
    justifyContent: "center",
    marginRight: WIDTH * 0.02,
  },
  mapContainer: {
    flex: 1,
    width: "100%",
    height: "70%",
    alignItems: "center",
    justifyContent: "center",
  },
  textCardContainer: {
    flexDirection: "row",
    alignSelf: "flex-end",
    width: "100%",
    height: "15%",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#F8F9FB",
    padding: 5,
  },
  addressContainer: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    width: "85%",
    height: "96%",
    backgroundColor: "white",
    borderRadius: 15,
    marginHorizontal: WIDTH * 0.01,
    borderColor: "gray",
    borderWidth: 0.4,
  },
  buttonsContainer: {
    flex: 1,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-evenly",
    width: "85%",
    height: "96%",
    backgroundColor: "white",
    borderRadius: 15,
    marginHorizontal: WIDTH * 0.01,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 8,
    },
    shadowOpacity: 0.46,
    shadowRadius: 11.14,
    elevation: 17,
  },
});
