import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  Dimensions,
  TouchableOpacity,
} from "react-native";
import "react-native-gesture-handler";
import { createStackNavigator } from "@react-navigation/stack";
import { FontAwesome5 } from "@expo/vector-icons";
import { LinearGradient } from "expo-linear-gradient";

const Stack = createStackNavigator();
const { width: WIDTH, height: HEIGHT } = Dimensions.get("screen");

const LoginCard = ({ parentCallback, currentUser }) => {
  const [user, setUser] = useState("");
  const [password, setPassword] = useState("");
  let userList = [
    {
      id: 1,
      name: "Gerardo Quiroga Jimenez",
      email: "cochiloco@gmail.com",
      phone: "6444545452",
      gender: "M",
      id_type: 2,
      userName: "cochiloco123",
      password: "1234",
      assignedBoss: false,
      status: true,
    },
    {
      id: 2,
      name: "Test Pass Parameters",
      email: "parameter@gmail.com",
      phone: "6345435643",
      gender: "M",
      id_type: 1,
      userName: "param",
      password: "54321",
      assignedBoss: false,
      status: true,
    },
    /*{ user_name: "test3", password: "1234", phone: "1", type: 2 },
    { user_name: "test4", password: "1234", phone: "1", type: 2 },*/
  ];

  _userLogin = () => {
    fetch("https://localhost/api/Usuario/Login/", {
      method: "POST",
      headers: {
        Authorization: "Basic " + Base64.btoa("jefeTest" + ":" + "jefe123"),
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((responseData) => {
        console.log(responseData);
      });
  };

  useEffect(() => {
    _userLogin();
  }, []);

  const sendData = () => {
    parentCallback(2);
  };

  const chars =
    "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/=";
  const Base64 = {
    btoa: (input = "") => {
      let str = input;
      let output = "";

      for (
        let block = 0, charCode, i = 0, map = chars;
        str.charAt(i | 0) || ((map = "="), i % 1);
        output += map.charAt(63 & (block >> (8 - (i % 1) * 8)))
      ) {
        charCode = str.charCodeAt((i += 3 / 4));

        if (charCode > 0xff) {
          throw new Error(
            "'btoa' failed: The string to be encoded contains characters outside of the Latin1 range."
          );
        }

        block = (block << 8) | charCode;
      }

      return output;
    },

    atob: (input = "") => {
      let str = input.replace(/=+$/, "");
      let output = "";

      if (str.length % 4 == 1) {
        throw new Error(
          "'atob' failed: The string to be decoded is not correctly encoded."
        );
      }
      for (
        let bc = 0, bs = 0, buffer, i = 0;
        (buffer = str.charAt(i++));
        ~buffer && ((bs = bc % 4 ? bs * 64 + buffer : buffer), bc++ % 4)
          ? (output += String.fromCharCode(255 & (bs >> ((-2 * bc) & 6))))
          : 0
      ) {
        buffer = chars.indexOf(buffer);
      }

      return output;
    },
  };

  const getMoviesFromApi = () => {
    return fetch("https://localhost/api/Usuario/GetUsuario/1")
      .then((response) => response.json())
      .then((json) => {
        return json;
      })
      .catch((error) => {
        console.error(error);
      });
  };

  const sendData2 = () => {
    parentCallback(3);
  };

  const signIn = () => {
    let userWasFound = false;

    for (let i = 0; i < userList.length; i++) {
      if (user === userList[i].userName && password === userList[i].password) {
        userWasFound = true;
        currentUser(userList[i]);
        break;
      }
    }
    if (!userWasFound) console.log("usuario no existe");
  };

  return (
    <View style={styles.container}>
      <View style={styles.cardHeader}>
        <LinearGradient
          colors={["#009EE3", "#0074A7", "#004360"]}
          style={{
            width: "100%",
            height: "100%",
            borderTopLeftRadius: 10,
            borderTopRightRadius: 10,
            alignItems: "center",
            justifyContent: "center",
            padding: 20,
          }}
        >
          <Text style={styles.titleText}>
            Sistema de reportes de servicios publicos
          </Text>
        </LinearGradient>
      </View>

      <View style={styles.cardContent}>
        <Text style={styles.welcomeText}>¡Bienvenido!</Text>

        <Text style={styles.elementText}>Usuario</Text>
        <View style={styles.textInputContainer}>
          <View style={styles.iconContainer}>
            <FontAwesome5 name="user-alt" size={16} color="black" />
          </View>
          <TextInput
            style={styles.textInput}
            onChangeText={(text) => setUser(text)}
            value={user}
          />
        </View>

        <Text style={styles.elementText}>Contraseña</Text>
        <View style={styles.textInputContainer}>
          <View style={styles.iconContainer}>
            <FontAwesome5 name="lock" size={16} color="black" />
          </View>
          <TextInput
            style={styles.textInput}
            secureTextEntry={true}
            onChangeText={(text) => setPassword(text)}
            value={password}
          />
        </View>

        <TouchableOpacity
          style={styles.forgotPasswordButton}
          onPress={sendData2}
        >
          <Text style={styles.SecondaryButtonText}>Olvidé mi contraseña*</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.logInButton}
          onPress={console.log("date")}
        >
          <Text style={styles.buttonText}>Iniciar sesión</Text>
        </TouchableOpacity>
      </View>

      <View style={styles.cardFooter}>
        <TouchableOpacity style={styles.incognitoButton}>
          <Text style={[styles.SecondaryButtonText, { color: "gray" }]}>
            Entrar como incognito
          </Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.registerButton} onPress={sendData}>
          <Text
            style={[
              styles.SecondaryButtonText,
              { color: "rgba(0, 158, 227, 1)" },
            ]}
          >
            Registrarse
          </Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

export default LoginCard;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "stretch",
    justifyContent: "center",
    width: "100%",
    borderRadius: 10,
  },
