    borderBottomRightRadius: 10,
  },
  elementText: {
    fontSize: HEIGHT * 0.02,
    fontWeight: "bold",
    marginBottom: HEIGHT * 0.004,
    alignSelf: "flex-start",
  },
  titleText: {
    fontSize: HEIGHT * 0.032,
    fontWeight: "bold",
    marginBottom: HEIGHT * 0.01,
    textAlign: "center",
    color: "white",
  },
  welcomeText: {
    fontSize: HEIGHT * 0.026,
    fontWeight: "bold",
    textAlign: "center",
  },
  iconContainer: {
    width: "7%",
    height: HEIGHT * 0.05,
    justifyContent: "center",
    alignItems: "center",
    //alignSelf: "flex-start",
  },
  textInputContainer: {
    borderWidth: 0.5,
    borderColor: "gray",
    backgroundColor: "transparent",
    width: "100%",
    height: HEIGHT * 0.05,
    borderRadius: 5,
    alignItems: "stretch",
    justifyContent: "center",
    marginBottom: WIDTH * 0.02,
    alignSelf: "flex-start",
    flexDirection: "row",
  },
  textInput: {
    width: "93%",
    height: HEIGHT * 0.05,
    alignItems: "center",
    justifyContent: "center",
    fontSize: HEIGHT * 0.021,
  },
  forgotPasswordButton: {
    width: "55%",
    height: HEIGHT * 0.04,
    borderRadius: 5,
    alignItems: "center",
    justifyContent: "center",
    marginBottom: WIDTH * 0.04,
    alignSelf: "flex-start",
  },
  SecondaryButtonText: {
    fontSize: HEIGHT * 0.018,
    alignSelf: "flex-start",
    fontWeight: "bold",
    color: "red",
  },
  logInButton: {
    width: "100%",
    height: HEIGHT * 0.05,
    backgroundColor: "rgba(0, 158, 227, 1)",
    borderRadius: 5,
    alignItems: "center",
    justifyContent: "center",
