import React from "react";
import {
  StyleSheet,
  View,
  Text,
  Dimensions,
  FlatList,
  TouchableOpacity,
} from "react-native";
import { Ionicons } from "@expo/vector-icons";
import { MaterialCommunityIcons } from "@expo/vector-icons";

const { width: WIDTH, height: HEIGHT } = Dimensions.get("screen");
let reports = [
  { id: "1", date: "10/01/2020", type: "Bacheo", status: "Abierto" },
  { id: "23", date: "12/01/2021", type: "Alcantarillado", status: "Abierto" },
  { id: "12345", date: "21/01/2021", type: "Alumbrado", status: "Inconcluso" },
  { id: "234", date: "31/01/2022", type: "Bacheo", status: "Abierto" },
  { id: "2", date: "18/01/2022", type: "Bacheo", status: "Abierto" },
  { id: "3", date: "28/01/2022", type: "Bacheo", status: "Abierto" },
];

const ReportListUser = ({ parentCallback, user }) => {
  const sendData = () => {
    parentCallback("NewReportScreen");
  };

  const sendReport = (item, screen) => {
    parentCallback(item, screen);
  };

  return (
    <View style={styles.container}>
      <View style={styles.tableHeader}>
        {user.id_type === 1 ? (
          <Text style={styles.headerText}>Mis reportes creados</Text>
        ) : (
          <Text style={styles.headerText}>Trabajos asignados</Text>
        )}
      </View>
      <View style={styles.tableElements}>
        <FlatList
          keyExtractor={(item) => item.id}
          data={reports}
          renderItem={({ item }) => (
            <View style={styles.reportRow}>
              <View style={[styles.reportItem, { flex: 1 }]}>
                <MaterialCommunityIcons
                  name="clipboard-text-outline"
                  size={28}
                  color="black"
                />
              </View>
              <View
                style={[
                  styles.reportItem,
                  { alignItems: "flex-start", flex: 2 },
                ]}
              >
                <Text style={styles.idText}>#{item.id}</Text>
                <Text style={styles.typeText}>{item.type}</Text>
              </View>
              <View style={[styles.reportItem, { alignItems: "flex-start" }]}>
                <Text style={styles.idText}>{item.date}</Text>
                <Text style={styles.elementText}>{item.status}</Text>
              </View>
              <View style={[styles.reportItem, { flex: 2, padding: 0 }]}>
                <TouchableOpacity
                  style={styles.button}
                  onPress={() => sendReport(item, 1)}
                >
                  <Text style={styles.buttonText}>Detalles</Text>
                </TouchableOpacity>
                {user.id_type === 2 ? (
                  <TouchableOpacity
                    style={styles.button}
                    onPress={() => sendReport(item, 2)}
                  >
                    <Text style={[styles.buttonText, { color: "#223995" }]}>
                      Modificar
                    </Text>
                  </TouchableOpacity>
                ) : null}
              </View>
            </View>
          )}
        />
      </View>
      <View style={styles.tableFooter}>
        {user.id_type === 1 ? (
          <TouchableOpacity style={styles.addButton} onPress={sendData}>
            <Ionicons
              name="md-add"
              size={WIDTH * 0.1}
              color="rgba(0,158,227,1)"
            />
          </TouchableOpacity>
        ) : null}
      </View>
    </View>
  );
};

export default ReportListUser;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "stretch",
    justifyContent: "center",
    width: "100%",
    borderRadius: 10,
  },
  tableHeader: {
    flex: 2,
    alignItems: "center",
    justifyContent: "space-evenly",
    flexDirection: "row",
    marginBottom: 2,
    backgroundColor: "white",
    borderTopLeftRadius: 10,
    borderTopRightRadius: 10,
  },
  tableElements: {
    flex: 20,
    alignItems: "stretch",
    justifyContent: "center",
  },
  reportRow: {
    flex: 1,
    flexDirection: "row",
    backgroundColor: "white",
    justifyContent: "center",
    margin: 2,
    borderRadius: 5,
  },
  reportItem: {
    paddingVertical: HEIGHT * 0.0085,
    flex: 2,
    alignItems: "center",
    justifyContent: "center",
    width: "25%",
  },
  button: {
    alignItems: "center",
    backgroundColor: "white",
    padding: 10,
    width: "85%",
    height: HEIGHT * 0.009,
    justifyContent: "center",
    marginBottom: HEIGHT * 0.004,
  },
  addButton: {
    alignItems: "center",
    backgroundColor: "white",
    borderRadius: 60,
    width: "15%",
    height: "80%",
    justifyContent: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 8,
      height: 8,
    },
    shadowOpacity: 0.46,
    shadowRadius: 11.14,
    elevation: 17,
  },
  buttonText: {
    fontSize: HEIGHT * 0.017,
    color: "rgba(0,158,227,1)",
    textAlign: "center",
    fontWeight: "bold",
  },
  tableFooter: {
    flex: 3,
    alignItems: "center",
    justifyContent: "space-evenly",
    flexDirection: "row",
    marginTop: 2,
    backgroundColor: "white",
    borderBottomLeftRadius: 10,
    borderBottomRightRadius: 10,
  },
  headerText: {
    fontSize: HEIGHT * 0.021,
    fontWeight: "bold",
  },
  typeText: {
    fontSize: HEIGHT * 0.017,
    color: "black",
    fontWeight: "bold",
  },
  idText: {
    fontSize: HEIGHT * 0.014,
    color: "gray",
  },
  elementText: {
    fontSize: HEIGHT * 0.017,
    color: "black",
  },
});
