import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  View,
  Text,
  Dimensions,
  TouchableOpacity,
  TextInput,
} from "react-native";
import { Picker } from "@react-native-community/picker";

const { width: WIDTH, height: HEIGHT } = Dimensions.get("screen");

const NewReportType = ({ parentCallback, typeSelected }) => {
  let options = ["", "Bacheo", "Alcantarillado", "Luces", "Agua", "Parque"];
  let isOptionalType = (type) => {
    for (let i = 0; i < 6; i++) if (type === options[i]) return true;
    return false;
  };

  const [selection, setSelection] = useState(typeSelected);
  const [value, setValue] = useState(
    !isOptionalType(typeSelected) ? "" + typeSelected : ""
  );

  useEffect(() => {
    if (typeSelected !== "") {
      if (!isOptionalType(selection)) {
        setSelection("Otro");
      }
    }
  }, []);

  const sendData = () => {
    let type = "";
    if (value !== "") type = value;
    else type = selection;
    parentCallback(2, type);
  };
  let otherInput = (
    <TextInput
      placeholder="Otro tipo"
      style={styles.textInput}
      onChangeText={(text) => setValue(text)}
      value={value}
    />
  );
  let nextButton = (
    <TouchableOpacity style={styles.nextButton} onPress={sendData}>
      <Text style={styles.buttonText}>Siguiente</Text>
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      <View style={styles.cardHeader}>
        <Text style={styles.headerText}>Tipo de reporte</Text>
      </View>

      <View style={styles.cardContent}>
        <Text style={styles.elementText}>Selecciona el tipo de reporte</Text>

        <View style={styles.pickerContainer}>
          <Picker
            style={styles.picker}
            selectedValue={selection}
            onValueChange={(itemValue, itemIndex) => {
              setSelection(itemValue);
              setValue("");
            }}
          >
            <Picker.Item label={options[0]} value={options[0]} />
            <Picker.Item label={options[1]} value={options[1]} />
            <Picker.Item label={options[2]} value={options[2]} />
            <Picker.Item label={options[3]} value={options[3]} />
            <Picker.Item label={options[4]} value={options[4]} />
            <Picker.Item label={options[5]} value={options[5]} />
            <Picker.Item label="Otro" value="Otro" />
          </Picker>
        </View>

        {selection === "Otro" ? otherInput : null}
      </View>

      <View style={styles.cardFooter}>
        {selection !== "Otro" && selection !== ""
          ? nextButton
          : value !== ""
          ? nextButton
          : null}
      </View>
    </View>
  );
};

export default NewReportType;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "stretch",
    justifyContent: "center",
    width: "100%",
    borderRadius: 10,
  },
  cardHeader: {
    flex: 2,
    alignItems: "center",
    justifyContent: "space-evenly",
    flexDirection: "row",
    marginBottom: 2,
    backgroundColor: "white",
    borderTopLeftRadius: 10,
    borderTopRightRadius: 10,
  },
  cardContent: {
    flex: 20,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "white",
  },
  cardFooter: {
    flex: 3,
    alignItems: "center",
    justifyContent: "flex-end",
    flexDirection: "row",
    marginTop: 2,
    backgroundColor: "white",
    borderBottomLeftRadius: 10,
    borderBottomRightRadius: 10,
  },
  headerText: {
    fontSize: HEIGHT * 0.022,
    fontWeight: "bold",
  },
  elementText: {
    fontSize: HEIGHT * 0.025,
    fontWeight: "bold",
    marginBottom: HEIGHT * 0.02,
  },
  picker: {
    height: HEIGHT * 0.08,
    width: WIDTH * 0.5,
    fontWeight: "bold",
  },
  pickerContainer: {
    borderWidth: 0.5,
    borderColor: "black",
    backgroundColor: "transparent",
    width: "55%",
    height: HEIGHT * 0.05,
    borderRadius: 5,
    alignItems: "center",
    justifyContent: "center",
    marginBottom: HEIGHT * 0.02,
  },
  textInput: {
    borderWidth: 0.5,
    borderColor: "black",
    backgroundColor: "transparent",
    width: "55%",
    height: HEIGHT * 0.05,
    borderRadius: 5,
    alignItems: "center",
    justifyContent: "center",
    fontSize: HEIGHT * 0.021,
    paddingLeft: WIDTH * 0.03,
  },
  nextButton: {
    backgroundColor: "rgba(0,158,227,1)",
    width: "25%",
    height: HEIGHT * 0.048,
    borderRadius: 5,
    alignItems: "center",
    justifyContent: "center",
    marginRight: WIDTH * 0.02,
  },
  buttonText: {
    fontSize: HEIGHT * 0.02,
    alignSelf: "center",
    color: "white",
  },
});
