import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  View,
  Text,
  Dimensions,
  TouchableOpacity,
  Image,
  ImageBackground,
} from "react-native";
import MapComponent from "../components/MapComponent";
import * as Location from "expo-location";
import { MaterialCommunityIcons } from "@expo/vector-icons";

const { width: WIDTH, height: HEIGHT } = Dimensions.get("screen");

const ReportDetails = ({ parentCallback, report }) => {
  let [images, setImages] = useState([
    "file:/data/user/0/host.exp.exponent/cache/ExperienceData/%2540anonymous%252FApp-Servicios-Publicos-3c4a68d9-9530-4184-aa3d-bdaa3ad4b538/ImagePicker/1f7247ec-28e7-4911-9d71-d5d577f0d5e9.jpg",
    "file:/data/user/0/host.exp.exponent/cache/ExperienceData/%2540anonymous%252FApp-Servicios-Publicos-3c4a68d9-9530-4184-aa3d-bdaa3ad4b538/ImagePicker/1a2ef59c-cc59-47ed-99ae-88f6cc53b42f.jpg",
    "file:/data/user/0/host.exp.exponent/cache/ExperienceData/%2540anonymous%252FApp-Servicios-Publicos-3c4a68d9-9530-4184-aa3d-bdaa3ad4b538/ImagePicker/1a2ef59c-cc59-47ed-99ae-88f6cc53b42f.jpg",
    "file:/data/user/0/host.exp.exponent/cache/ExperienceData/%2540anonymous%252FApp-Servicios-Publicos-3c4a68d9-9530-4184-aa3d-bdaa3ad4b538/ImagePicker/1a2ef59c-cc59-47ed-99ae-88f6cc53b42f.jpg",
  ]);
  const [address, setAddress] = useState({
    latitude: 27.516458,
    longitude: -109.948725,
  });
  const [textAddress, setTextAddress] = useState("");

  useEffect(() => {
    if (images.length >= 1)
      setImages(images.filter((image) => image != undefined));
    ReverseGeocode(address);
  }, []);

  let ReverseGeocode = async (ad) => {
    let data = await Location.reverseGeocodeAsync({
      latitude: ad.latitude,
      longitude: ad.longitude,
    });
    setTextAddress(
      data[0].street +
        " #" +
        data[0].name +
        ", col. " +
        data[0].district +
        " CP. " +
        data[0].postalCode
    );
  };

  const sendData = () => {
    parentCallback(null);
  };

  return (
    <View style={styles.container}>
      <View style={styles.cardHeader}>
        <Text style={styles.headerText}>Reporte #{report.id}</Text>
      </View>

      <View style={styles.cardContent}>
        <View style={styles.imageButtonsContainer}>
          <View style={styles.addressContainer}>
            <Text style={styles.elementText}>Tipo: {report.type}</Text>
            <Text style={styles.elementText}>Estado: {report.status}</Text>
            <Text style={styles.elementText}>Fecha: {report.date}</Text>
            <Text style={styles.addressText}>{textAddress}</Text>
          </View>
          <View style={styles.mapContainer}>
            <MapComponent parentLocation={address} parentCallback={null} />
          </View>
        </View>

        <View style={styles.imagesContainer}>
          <View style={styles.imagesTrioContainer}>
            <View style={styles.imageElement}>
              {images[0] !== undefined ? (
                <ImageBackground
                  source={{ uri: images[0] }}
                  resizeMode="stretch"
                  style={{ height: "100%", width: "100%" }}
                ></ImageBackground>
              ) : (
                <MaterialCommunityIcons
                  name="image-off"
                  size={24}
                  color="black"
                />
              )}
            </View>
            <View style={styles.imageElement}>
              {images.length >= 2 && images[1] !== undefined ? (
                <ImageBackground
                  source={{ uri: images[1] }}
                  resizeMode="stretch"
                  style={{ height: "100%", width: "100%" }}
                ></ImageBackground>
              ) : (
                <MaterialCommunityIcons
                  name="image-off"
                  size={24}
                  color="black"
                />
              )}
            </View>
          </View>
          <View style={styles.imagesTrioContainer}>
            <View style={styles.imageElement}>
              {images.length >= 3 && images[2] !== undefined ? (
                <ImageBackground
                  source={{ uri: images[2] }}
                  resizeMode="stretch"
                  style={{ height: "100%", width: "100%" }}
                ></ImageBackground>
              ) : (
                <MaterialCommunityIcons
                  name="image-off"
                  size={24}
                  color="black"
                />
              )}
            </View>
            <View style={styles.imageElement}>
              {images.length === 4 && images[3] !== undefined ? (
                <ImageBackground
                  source={{ uri: images[3] }}
                  resizeMode="stretch"
                  style={{ height: "100%", width: "100%" }}
                ></ImageBackground>
              ) : (
                <MaterialCommunityIcons
                  name="image-off"
                  size={24}
                  color="black"
                />
              )}
            </View>
          </View>
        </View>
      </View>

      <View style={styles.cardFooter}>
        <TouchableOpacity style={styles.goBackButton} onPress={sendData}>
          <Text style={styles.buttonText}>Volver</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

export default ReportDetails;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "stretch",
    justifyContent: "center",
    width: "100%",
    borderRadius: 10,
  },
  cardHeader: {
    flex: 2,
    alignItems: "center",
    justifyContent: "space-evenly",
    flexDirection: "row",
    marginBottom: 2,
    backgroundColor: "white",
    borderTopLeftRadius: 10,
    borderTopRightRadius: 10,
  },
  cardContent: {
    flex: 20,
    alignItems: "flex-start",
    justifyContent: "center",
    backgroundColor: "white",
  },
  elementText: {
    fontSize: HEIGHT * 0.018,
    color: "black",
    alignSelf: "flex-start",
    fontWeight: "bold",
    marginLeft: WIDTH * 0.02,
  },
  addressText: {
    fontSize: HEIGHT * 0.018,
    color: "black",
    textAlign: "center",
    fontWeight: "bold",
    marginTop: HEIGHT * 0.014,
    marginHorizontal: WIDTH * 0.02,
  },
  cardFooter: {
    flex: 3,
    alignItems: "center",
    justifyContent: "flex-end",
    flexDirection: "row",
    marginTop: 2,
    backgroundColor: "white",
    borderBottomLeftRadius: 10,
    borderBottomRightRadius: 10,
  },
  headerText: {
    fontSize: HEIGHT * 0.022,
    fontWeight: "bold",
  },
  buttonText: {
    fontSize: HEIGHT * 0.02,
    alignSelf: "center",
    color: "white",
  },
  goBackButton: {
    backgroundColor: "#CCD1D1",
    width: "25%",
    height: HEIGHT * 0.048,
    borderRadius: 5,
    alignItems: "center",
    justifyContent: "center",
    marginRight: WIDTH * 0.02,
  },
  imagesContainer: {
    flex: 1,
    width: "100%",
    height: "55%",
    alignItems: "center",
    justifyContent: "center",
  },
  imagesTrioContainer: {
    flex: 1,
    width: "100%",
    backgroundColor: "#E5E7E9",
    alignItems: "center",
    justifyContent: "center",
    flexDirection: "row",
    paddingVertical: "0.5%",
  },
  imageElement: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    width: "100%",
    height: "100%",
    margin: "0.5%",
  },
  imageButtonsContainer: {
    flexDirection: "row",
    alignSelf: "flex-end",
    width: "100%",
    height: "45%",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#F2F3F5",
  },
  addressContainer: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    width: "50%",
    height: "96%",
    backgroundColor: "white",
    borderRadius: 15,
    marginHorizontal: WIDTH * 0.01,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 8,
    },
    shadowOpacity: 0.46,
    shadowRadius: 11.14,
    elevation: 17,
  },
  mapContainer: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    width: "50%",
    height: "100%",
    backgroundColor: "#F2F3F5",
  },
});
