import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  Dimensions,
  TouchableOpacity,
} from "react-native";
import eMail from "react-native-email";
import { AntDesign } from "@expo/vector-icons";

const { width: WIDTH, height: HEIGHT } = Dimensions.get("screen");
let userList = [
  { id: "1", name: "telera" },
  { id: "2", name: "lalala" },
  { id: "3", name: "geraa329" },
  { id: "4", name: "fditto" },
];

const ForgotPasswordCard = ({ parentCallback }) => {
  const [borderColor, setBorderColor] = useState("#000");

  const sendData = () => {
    parentCallback(1);
  };

  const [email, setEmail] = useState("");

  let handleEmail = () => {
    const to = [email]; // string or array of email addresses
    eMail(to, {
      // Optional additional arguments
      cc: ["bazzy@moo.com", "doooo@daaa.com"], // string or array of email addresses
      bcc: "mee@mee.com", // string or array of email addresses
      subject: "Show how to use",
      body: "Some body right here",
    }).catch(console.error);
  };

  let validateEmail = (email) => {
    const re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(String(email).toLowerCase());
  };

  let validateField = () => {
    if (!validateEmail(email)) setBorderColor("red");
    else setBorderColor("#000");
  };

  return (
    <View style={styles.container}>
      <View style={styles.cardHeader}>
        <Text style={styles.headerText}>Recuperar contraseña</Text>
      </View>

      <View style={styles.cardContent}>
        <AntDesign name="mail" size={WIDTH * 0.2} color="black" />
        <Text style={styles.instructionText}>
          Porfavor ingresa el correo asociado a tu cuenta para recuperar tu
          contraseña
        </Text>
        <Text style={styles.elementText}>Correo electrónico</Text>
        <TextInput
          style={[styles.textInput, { borderColor: borderColor }]}
          onChangeText={(text) => setEmail(text)}
          value={email}
          onFocus={() => setBorderColor("rgba(0,158,227,1)")}
          onBlur={() => validateField()}
        />
        {borderColor === "red" ? (
          <Text style={styles.errorMsg}>el correo ingresado es invalido</Text>
        ) : null}
      </View>

      <View style={styles.cardFooter}>
        <TouchableOpacity
          style={[styles.nextButton, { backgroundColor: "#CCD1D1" }]}
          onPress={sendData}
        >
          <Text style={styles.buttonText}>Cancelar</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.nextButton} onPress={sendData}>
          <Text style={styles.buttonText}>Enviar</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

export default ForgotPasswordCard;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "stretch",
    justifyContent: "center",
    width: "100%",
    borderRadius: 10,
  },
  cardHeader: {
    flex: 3,
    alignItems: "center",
    justifyContent: "space-evenly",
    flexDirection: "row",
    marginBottom: 2,
    backgroundColor: "white",
    borderTopLeftRadius: 10,
    borderTopRightRadius: 10,
  },
  cardContent: {
    flex: 20,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "white",
    paddingHorizontal: WIDTH * 0.02,
    paddingVertical: HEIGHT * 0.03,
  },
  elementText: {
    fontSize: HEIGHT * 0.019,
    fontWeight: "bold",
    alignSelf: "flex-start",
    marginRight: WIDTH * 0.29,
    marginTop: WIDTH * 0.014,
  },
  cardFooter: {
    flex: 3,
    alignItems: "center",
    justifyContent: "flex-end",
    flexDirection: "row",
    marginTop: 2,
    backgroundColor: "white",
    borderBottomLeftRadius: 10,
    borderBottomRightRadius: 10,
  },
  headerText: {
    fontSize: HEIGHT * 0.024,
    fontWeight: "bold",
  },
  buttonText: {
    fontSize: HEIGHT * 0.02,
    alignSelf: "center",
    color: "white",
  },
  nextButton: {
    backgroundColor: "rgba(0,158,227,1)",
    width: "25%",
    height: HEIGHT * 0.048,
    borderRadius: 5,
    alignItems: "center",
    justifyContent: "center",
    marginRight: WIDTH * 0.02,
  },
  textInput: {
    borderWidth: 0.5,
    borderColor: "gray",
    backgroundColor: "transparent",
    width: "100%",
    height: HEIGHT * 0.043,
    borderRadius: 5,
    alignItems: "center",
    justifyContent: "center",
    fontSize: HEIGHT * 0.018,
    paddingLeft: WIDTH * 0.015,
    alignSelf: "flex-start",
  },
  errorMsg: {
    fontSize: HEIGHT * 0.016,
    color: "red",
    alignSelf: "flex-start",
  },
  instructionText: {
    fontSize: HEIGHT * 0.02,
    color: "black",
    marginBottom: HEIGHT * 0.01,
    textAlign: "center",
    marginTop: HEIGHT * 0.01,
  },
});
