import React, { useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  Dimensions,
  TouchableOpacity,
} from "react-native";
import EditReportHeader from "../components/EditReportHeader";
import NewReportImages from "../components/NewReportImages";
import NewReportFinished from "../components/NewReportFinished";
import ReportDelay from "../components/ReportDelay";
import Modal from "react-native-modal";

const { width: WIDTH, height: HEIGHT } = Dimensions.get("screen");

const EditReportScreen = ({ route, navigation }) => {
  const { report, firstScreen } = route.params;
  const [images, setImages] = useState([]);
  const [screen, setScreen] = useState(firstScreen);
  const [visibleModal, setVisibleModal] = useState(null);

  const back = (childData) => {
    if (screen === 1 || screen === 3) navigation.goBack();
  };

  const callbackFunction = (childData, reportValue) => {
    switch (childData) {
      case 4:
        setImages(reportValue);
        setVisibleModal(1);
        break;
      case 10:
        navigation.navigate("MainMenu");
        break;
    }
  };

  let _renderButton = (text, color, onPress) => (
    <TouchableOpacity onPress={onPress}>
      <View style={[styles.button, { backgroundColor: color }]}>
        <Text style={styles.buttonText}>{text}</Text>
      </View>
    </TouchableOpacity>
  );

  let _renderModalContent = () => (
    <View style={styles.modalContent}>
      <Text style={[styles.elementText, { textAlign: "center" }]}>
        ¿Estas seguro que deseas cerrar el reporte?
      </Text>

      <View style={styles.modalButtons}>
        {_renderButton("Aceptar", "rgba(0,158,227,1)", () => {
          setScreen(2);
          setVisibleModal(null);
        })}
        {_renderButton("Rechazar", "#CCD1D1", () => {
          setVisibleModal(null);
        })}
      </View>
    </View>
  );

  let reportImages = (
    <NewReportImages parentCallback={callbackFunction} currentImages={images} />
  );
  let reportDelay = (
    <ReportDelay parentCallback={callbackFunction} currentImages={images} />
  );
  let reportFinished = (
    <NewReportFinished
      parentCallback={callbackFunction}
      title="Reporte cerrado"
      MessageHeader="¡Felicidades!"
      Message="El reporte fue cerrado con exito"
      success={true}
    />
  );

  return (
    <View style={styles.container}>
      <View style={styles.headerBar}>
        <EditReportHeader parentCallback={back} screen={screen} />
      </View>

      <View style={styles.card}>
        {screen === 1
          ? reportImages
          : screen === 2
          ? reportFinished
          : reportDelay}
      </View>
      <Modal isVisible={visibleModal === 1}>{_renderModalContent()}</Modal>
    </View>
  );
};

export default EditReportScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#E5E7E9",
    alignItems: "stretch",
    justifyContent: "center",
    width: "100%",
  },
  headerBar: {
    flex: 5,
    alignItems: "center",
    justifyContent: "center",
    borderBottomLeftRadius: 15,
    borderBottomRightRadius: 15,
  },
  card: {
    flex: 16,
    backgroundColor: "#E5E7E9",
    flexDirection: "row",
    alignItems: "stretch",
    justifyContent: "center",
    marginHorizontal: WIDTH * 0.015,
    borderRadius: 10,
    marginTop: HEIGHT * 0.01,
    marginBottom: HEIGHT * 0.01,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 8,
    },
    shadowOpacity: 0.46,
    shadowRadius: 11.14,
    elevation: 17,
  },
  reportsCard: {
    flex: 13,
    backgroundColor: "#E5E7E9",
    alignItems: "stretch",
    borderRadius: 10,
    marginHorizontal: WIDTH * 0.02,
    marginBottom: HEIGHT * 0.01,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 8,
    },
    shadowOpacity: 0.46,
    shadowRadius: 11.14,
    elevation: 17,
  },
  button: {
    width: WIDTH * 0.3,
    height: HEIGHT * 0.048,
    borderRadius: 5,
    alignItems: "center",
    justifyContent: "center",
    marginBottom: 10,
  },
  modalContent: {
    backgroundColor: "white",
    justifyContent: "center",
    alignItems: "center",
    borderRadius: 4,
    borderColor: "rgba(0, 0, 0, 0.1)",
    width: "100%",
    height: HEIGHT * 0.25,
    padding: 22,
  },
  elementText: {
    fontSize: HEIGHT * 0.023,
    marginBottom: HEIGHT * 0.02,
    alignSelf: "center",
    fontWeight: "bold",
  },
  buttonText: {
    fontSize: HEIGHT * 0.02,
    alignSelf: "center",
    color: "white",
  },
  modalButtons: {
    flexDirection: "row",
    marginTop: HEIGHT * 0.02,
    alignSelf: "center",
    width: "90%",
    height: "40%",
    alignItems: "center",
    justifyContent: "space-evenly",
  },
});
