import React, { useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  Dimensions,
  TextInput,
  TouchableOpacity,
  ActivityIndicator,
} from "react-native";
import { MaterialIcons } from "@expo/vector-icons";

const { width: WIDTH, height: HEIGHT } = Dimensions.get("screen");

const LocationForm = ({
  parentCallback,
  locationAdded,
  optionalAddressForm,
  dataForm,
}) => {
  const [exist, setExist] = useState(locationAdded);
  const [loading, setLoading] = useState(false);
  let [address, setAddress] = useState(dataForm);

  const sendData = () => {
    setExist(false);
    setLoading(true);
    parentCallback(1);
  };

  const sendData2 = () => {
    setExist(false);
    setLoading(true);
    parentCallback(4);
  };

  const sendAddress = () => {
    optionalAddressForm(address);
  };

  return (
    <View style={styles.cardContent}>
      <View
        style={{
          flexDirection: "row",
          alignSelf: "flex-start",
        }}
      >
        <TouchableOpacity
          style={[styles.gpsButton, { marginRight: WIDTH * 0.03 }]}
          onPress={sendData}
        >
          <MaterialIcons
            name="location-searching"
            size={HEIGHT * 0.028}
            color="#335CDC"
          />
          <Text style={[styles.gpsButtonText, { color: "#335CDC" }]}>
            Buscar ubicación
          </Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.gpsButton} onPress={sendData2}>
          <MaterialIcons
            name="my-location"
            size={HEIGHT * 0.028}
            color="#223995"
          />
          <Text style={styles.gpsButtonText}>Ubicación actual</Text>
        </TouchableOpacity>
      </View>
      {exist ? (
        <Text style={styles.validatedAddress}>
          ¡Ubicación establecida correctamente!
        </Text>
      ) : (
        <ActivityIndicator
          size="small"
          color="rgba(0,158,227,1)"
          animating={loading}
          style={{ marginTop: HEIGHT * 0.02 }}
        />
      )}

      <Text style={styles.optionalText}>Información opcional</Text>
      <Text style={styles.elementText}>Calle y número</Text>
      <TextInput
        style={styles.textInput}
        onBlur={() => sendAddress()}
        onChangeText={(text) => setAddress({ ...address, street: text })}
        value={address.street}
      />
      <Text style={styles.elementText}>Entre</Text>
      <View
        style={{
          flexDirection: "row",
          alignSelf: "flex-start",
        }}
      >
        <TextInput
          style={[styles.textInput, { width: "46.8%" }]}
          onBlur={() => sendAddress()}
          onChangeText={(text) => setAddress({ ...address, street_1: text })}
          value={address.street_1}
        />
        <Text style={styles.betweenStreetsText}>y</Text>
        <TextInput
          style={[styles.textInput, { width: "46.8%" }]}
          onBlur={() => sendAddress()}
          onChangeText={(text) => setAddress({ ...address, street_2: text })}
          value={address.street_2}
        />
      </View>
      <Text style={styles.elementText}>Referencias</Text>
      <TextInput
        style={styles.textInput}
        onBlur={() => sendAddress()}
        onChangeText={(text) => setAddress({ ...address, references: text })}
        value={address.references}
      />
      <Text style={styles.elementText}>Colonia</Text>
      <TextInput
        style={styles.textInput}
        onBlur={() => sendAddress()}
        onChangeText={(text) => setAddress({ ...address, neighborhood: text })}
        value={address.neighborhood}
      />
    </View>
  );
};

export default LocationForm;

const styles = StyleSheet.create({
  cardContent: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "white",
    paddingHorizontal: WIDTH * 0.02,
    paddingVertical: HEIGHT * 0.03,
    width: "100%",
    height: "100%",
  },
  gpsButton: {
    backgroundColor: "white",
    width: "48%",
    height: "130%",
    borderRadius: 5,
    alignItems: "center",
    justifyContent: "center",
    marginBottom: WIDTH * 0.02,
    flexDirection: "row",
  },
  gpsButtonText: {
    fontSize: HEIGHT * 0.021,
    alignSelf: "center",
    fontWeight: "bold",
    color: "#223995",
    textAlign: "center",
  },
  buttonText: {
    fontSize: HEIGHT * 0.02,
    alignSelf: "center",
    color: "white",
    textAlign: "center",
  },
  elementText: {
    fontSize: HEIGHT * 0.02,
    fontWeight: "bold",
    marginBottom: HEIGHT * 0.004,
    alignSelf: "flex-start",
  },
  optionalText: {
    fontSize: HEIGHT * 0.019,
    color: "gray",
    alignSelf: "flex-start",
    fontStyle: "italic",
    marginTop: HEIGHT * 0.016,
  },
  validatedAddress: {
    fontSize: HEIGHT * 0.019,
    color: "#28BF07",
    marginTop: HEIGHT * 0.02,
  },
  textInput: {
    borderWidth: 0.5,
    borderColor: "gray",
    backgroundColor: "transparent",
    width: "100%",
    height: HEIGHT * 0.044,
    borderRadius: 5,
    alignItems: "center",
    justifyContent: "center",
    fontSize: HEIGHT * 0.019,
    paddingLeft: WIDTH * 0.015,
    marginBottom: WIDTH * 0.02,
    alignSelf: "flex-start",
  },
  betweenStreetsText: {
    alignSelf: "center",
    fontSize: HEIGHT * 0.021,
    marginHorizontal: "2%",
  },
});
