import React, { useState } from "react";
import { StyleSheet, View, Text, Dimensions, SafeAreaView } from "react-native";
import WelcomeBar from "../components/WelcomeBar";
import CurrentDate from "../components/CurrentDate";
import ReportList from "../components/ReportList";
import ReportListUser from "../components/ReportListUser";
import ReportDetails from "../components/ReportDetails";
import ModifyReport from "../components/ModifyReport";

const { width: WIDTH, height: HEIGHT } = Dimensions.get("screen");

const MainMenu = ({ route, navigation }) => {
  const { user } = route.params;

  const [report, setReport] = useState(null);
  const [screen, setScreen] = useState(1);

  const callbackFunction = (childData, value) => {
    if (childData === "NewReportScreen") navigation.navigate(childData);
    else {
      setReport(childData);
      setScreen(value);
    }
  };

  const closeReport = (childData, value) => {
    navigation.navigate(childData, {
      report: report,
      firstScreen: value,
    });
  };

  let reportDetails = (
    <ReportDetails report={report} parentCallback={callbackFunction} />
  );

  let modifyReport = (
    <ModifyReport
      report={report}
      parentCallback={callbackFunction}
      EditReport={closeReport}
    />
  );

  /*const [user, setUser] = useState({
    id: 1,
    name: "Gerardo Quiroga Jimenez",
    email: "cochiloco@gmail.com",
    phone: "6444545452",
    gender: "M",
    id_type: 2,
    userName: "cochiloco123",
  });*/

  return (
    <View style={styles.container}>
      <View style={styles.headerBar}>
        <WelcomeBar user={user} />
      </View>
      <View style={styles.dateCard}>
        <CurrentDate />
      </View>
      <View style={styles.reportsCard}>
        {
          /*user.id_type === 1 ? (
          <ReportListUser parentCallback={callbackFunction} />
        ) : (
          <ReportList />
        )*/
          report === null ? (
            <ReportListUser parentCallback={callbackFunction} user={user} />
          ) : screen === 1 ? (
            reportDetails
          ) : (
            modifyReport
          )
          //reportDetails
        }
      </View>
    </View>
  );
};

export default MainMenu;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#E5E7E9",
    alignItems: "stretch",
    justifyContent: "center",
    width: "100%",
  },
  headerBar: {
    flex: 5,
    alignItems: "center",
    justifyContent: "center",
    borderBottomLeftRadius: 15,
    borderBottomRightRadius: 15,
  },
  dateCard: {
    flex: 1,
    backgroundColor: "red",
    alignItems: "center",
    justifyContent: "center",
    marginHorizontal: WIDTH * 0.05,
    borderRadius: 10,
    marginTop: -(HEIGHT * 0.02),
    marginBottom: HEIGHT * 0.01,
  },
  reportsCard: {
    flex: 15,
    backgroundColor: "#E5E7E9",
    alignItems: "center",
    borderRadius: 10,
    marginHorizontal: WIDTH * 0.02,
    marginBottom: HEIGHT * 0.01,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 8,
    },
    shadowOpacity: 0.46,
    shadowRadius: 11.14,
    elevation: 17,
  },
});
