import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  Dimensions,
  TouchableOpacity,
} from "react-native";
import { Picker } from "@react-native-community/picker";

const { width: WIDTH, height: HEIGHT } = Dimensions.get("screen");
let userList = [
  { id: "1", name: "telera" },
  { id: "2", name: "lalala" },
  { id: "3", name: "geraa329" },
  { id: "4", name: "fditto" },
];

const RegisterCard = ({ parentCallback }) => {
  const sendData = () => {
    parentCallback(null);
  };

  const sendUser = () => {
    console.log(user);
    parentCallback(user);
  };

  const [gender, setGender] = useState("Masculino");
  let [user, setUser] = useState({
    name: "",
    phone: "",
    user_gender: "",
    email: "",
    userName: "",
    password: "",
    confirmPassword: "",
  });
  let [border, setBorder] = useState({
    nameColor: "#000",
    phoneColor: "#000",
    emailColor: "#000",
    userNameColor: "#000",
    passwordColor: "#000",
    confirmPasswordColor: "#000",
  });
  let changeBorder = (field, color) => {
    switch (field) {
      case 1:
        setBorder({ ...border, nameColor: color });
        break;
      case 2:
        setBorder({ ...border, phoneColor: color });
        break;
      case 3:
        setBorder({ ...border, emailColor: color });
        break;
      case 4:
        setBorder({ ...border, userNameColor: color });
        break;
      case 5:
        setBorder({ ...border, passwordColor: color });
        break;
      case 6:
        setBorder({ ...border, confirmPasswordColor: color });
        break;
    }
    //setBorder({ nameColor: color });
  };

  let validateUserName = (userName) => {
    let exist = false;
    for (const user of userList) {
      if (userName.trim().toLowerCase() === user.name.trim().toLowerCase()) {
        exist = true;
        break;
      }
    }
    return exist;
  };

  let isNumeric = (n) => {
    return !isNaN(parseFloat(n)) && isFinite(n);
  };

  let validateField = (field) => {
    switch (field) {
      case 1:
        if (user.name.trim() === "") changeBorder(field, "red");
        else changeBorder(field, "#000");
        break;
      case 2:
        if (user.phone.trim().length !== 10 || !isNumeric(user.phone.trim()))
          changeBorder(field, "red");
        else changeBorder(field, "#000");
        break;
      case 3:
        if (!validateEmail(user.email)) changeBorder(field, "red");
        else changeBorder(field, "#000");
        break;
      case 4:
        if (validateUserName(user.userName) || user.userName.trim() === "")
          changeBorder(field, "red");
        else changeBorder(field, "#000");
        break;
      case 5:
        if (user.password.trim() === "") changeBorder(field, "red");
        else changeBorder(field, "#000");
        break;
      case 6:
        if (
          user.confirmPassword.trim() === "" ||
          user.confirmPassword.trim() !== user.password.trim()
        )
          changeBorder(field, "red");
        else changeBorder(field, "#000");
        break;
    }
  };

  let validateForm = () => {
    if (user.name.trim() === "") changeBorder(1, "red");
    else {
      if (user.phone.trim().length !== 10 || !isNumeric(user.phone.trim()))
        changeBorder(2, "red");
      else {
        if (!validateEmail(user.email)) changeBorder(3, "red");
        else {
          if (validateUserName(user.userName) || user.userName.trim() === "")
            changeBorder(4, "red");
          else {
            if (user.password.trim() === "") changeBorder(5, "red");
            else {
              if (
                user.confirmPassword.trim() === "" ||
                user.confirmPassword.trim() !== user.password.trim()
              )
                changeBorder(6, "red");
              else {
                sendUser();
              }
            }
          }
        }
      }
    }
  };

  let validateEmail = (email) => {
    const re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(String(email).toLowerCase());
  };

  return (
    <View style={styles.container}>
      <View style={styles.cardHeader}>
        <Text style={styles.headerText}>Ingresa tus datos</Text>
      </View>

      <View style={styles.cardContent}>
        <Text style={styles.elementText}>Nombre completo</Text>
        <TextInput
          style={[styles.textInput, { borderColor: border.nameColor }]}
          onFocus={() => changeBorder(1, "rgba(0,158,227,1)")}
          onBlur={() => validateField(1)}
          onChangeText={(text) => setUser({ ...user, name: text })}
          value={user.name}
        />
        <View style={styles.rowContent}>
          <Text style={styles.elementText}>Teléfono</Text>
          <Text style={styles.elementText}>Sexo</Text>
        </View>
        <View style={styles.rowContent}>
          <TextInput
            style={[
              styles.textInput,
              {
                width: "49%",
                marginRight: WIDTH * 0.01,
                borderColor: border.phoneColor,
              },
            ]}
            onFocus={() => changeBorder(2, "rgba(0,158,227,1)")}
            onBlur={() => validateField(2)}
            onChangeText={(text) => setUser({ ...user, phone: text })}
            value={user.phone}
            keyboardType="numeric"
          />
          <View style={styles.pickerContainer}>
            <Picker
              style={styles.picker}
              selectedValue={gender}
              onValueChange={(itemValue, itemIndex) => {
                setGender(itemValue);
                setUser({ ...user, user_gender: itemValue.charAt(0) });
              }}
            >
              <Picker.Item label="Masculino" value="Masculino" />
              <Picker.Item label="Femenino" value="Femenino" />
            </Picker>
          </View>
        </View>
        <Text style={styles.elementText}>Correo electronico</Text>
        <TextInput
          style={[styles.textInput, { borderColor: border.emailColor }]}
          autoCompleteType="email"
          onFocus={() => changeBorder(3, "rgba(0,158,227,1)")}
          onBlur={() => validateField(3)}
          onChangeText={(text) => setUser({ ...user, email: text })}
          value={user.email}
        />
        <Text style={styles.elementText}>Nombre de usuario</Text>
        <TextInput
          style={[styles.textInput, , { borderColor: border.userNameColor }]}
          onFocus={() => changeBorder(4, "rgba(0,158,227,1)")}
          onBlur={() => validateField(4)}
          onChangeText={(text) => setUser({ ...user, userName: text })}
          value={user.userName}
        />
        <Text style={styles.elementText}>Contraseña</Text>
        <TextInput
          style={[styles.textInput, { borderColor: border.passwordColor }]}
          onFocus={() => changeBorder(5, "rgba(0,158,227,1)")}
          onBlur={() => validateField(5)}
          onChangeText={(text) => setUser({ ...user, password: text })}
          value={user.password}
          secureTextEntry={true}
        />
        <Text style={styles.elementText}>Confirmar contraseña</Text>
        <TextInput
          style={[
            styles.textInput,
            { borderColor: border.confirmPasswordColor },
          ]}
          onFocus={() => changeBorder(6, "rgba(0,158,227,1)")}
          onBlur={() => validateField(6)}
          onChangeText={(text) => setUser({ ...user, confirmPassword: text })}
          value={user.confirmPassword}
          secureTextEntry={true}
        />
      </View>

      <View style={styles.cardFooter}>
        <TouchableOpacity
          style={[styles.nextButton, { backgroundColor: "#CCD1D1" }]}
          onPress={sendData}
        >
          <Text style={styles.buttonText}>Cancelar</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.nextButton}
          onPress={() => validateForm()}
        >
          <Text style={styles.buttonText}>Registrar</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

