import React, { useState } from "react";
import { StyleSheet, View, Dimensions, Text } from "react-native";
import LoginCard from "../components/LoginCard";
import RegisterCard from "../components/RegisterCard";
import ForgotPasswordCard from "../components/ForgotPasswordCard";
import CityImages from "../components/CityImages";

const { width: WIDTH, height: HEIGHT } = Dimensions.get("screen");

const LoginScreen = ({ navigation }) => {
  const [screen, setScreen] = useState(1);

  let isNumeric = (n) => {
    return !isNaN(parseFloat(n)) && isFinite(n);
  };

  const callbackFunction = (childData) => {
    if (isNumeric(childData)) setScreen(childData);
    else {
      if (childData === null) setScreen(1);
      else {
        console.log(childData);
        navigation.navigate("MainMenu");
      }
    }
  };

  const signIn = (gottenUser) => {
    console.log(gottenUser);
    navigation.navigate("MainMenu", {
      user: gottenUser,
    });
  };

  let loginCard = (
    <LoginCard parentCallback={callbackFunction} currentUser={signIn} />
  );
  let registerCard = <RegisterCard parentCallback={callbackFunction} />;
  let forgotPasswordCard = (
    <ForgotPasswordCard parentCallback={callbackFunction} />
  );

  return (
    <View style={styles.mainContainer}>
      <CityImages />

      <View style={styles.loginContainer}>
        {screen === 1
          ? loginCard
          : screen === 2
          ? registerCard
          : forgotPasswordCard}
      </View>

      <Text style={styles.bottomText}>Cd. Obreg√≥n, Sonora</Text>
    </View>
  );
};

export default LoginScreen;

const styles = StyleSheet.create({
  mainContainer: {
    flex: 1,
    backgroundColor: "#E5E7E9",
    justifyContent: "center",
    alignItems: "stretch",
    width: "100%",
  },
  loginContainer: {
    flex: 2,
    backgroundColor: "#E5E7E9",
    marginTop: -(WIDTH * 0.03),
    alignItems: "center",
    borderRadius: 10,
    marginHorizontal: WIDTH * 0.03,
    //marginBottom: HEIGHT * 0.02,
    justifyContent: "center",
    resizeMode: "cover",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 8,
    },
    shadowOpacity: 0.46,
    shadowRadius: 11.14,
    elevation: 17,
  },
  bottomText: {
    textAlign: "center",
    justifyContent: "center",
    alignSelf: "center",
    marginVertical: HEIGHT * 0.01,
    fontSize: HEIGHT * 0.015,
    color: "gray",
  },
});
